import React, { useEffect, useState } from 'react';
import ClientServices from '../../services/ClientService'; // Assure-toi que le chemin est correct

const ClientList = () => {
  const [clients, setClients] = useState([]); // Utilisation de useState pour stocker les clients
  const [loading, setLoading] = useState(true); // Pour afficher un état de chargement
  const [error, setError] = useState(null); // Pour gérer les erreurs éventuelles

  // Utilisation de useEffect pour charger les données à la première montée du composant
  useEffect(() => {
    const loadClients = async () => {
      try {
        const response = await ClientServices.findALL(); // Appel à l'API pour récupérer les clients
        setClients(response.data); // Mise à jour de l'état avec les données des clients
        setLoading(false); 
      } catch (error) {
        setError("Erreur lors du chargement des clients");
        setLoading(false); 
      }
    };

    loadClients(); 
  }, []); 

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Liste des Clients</h1>
      {clients.length > 0 ? (
        <table className='table'>
          <thead>
            <tr>
              <th scope='col'>Nom</th>
              <th scope='col'>Âge</th>
            </tr>
          </thead>
          <tbody>
            {clients.map((client) => (
              <tr key={client.id}>
                <td>{client.nom}</td>
                <td>{client.age}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Aucun client disponible.</p>
      )}
    </div>
  );
};

export default ClientList;
