import React, { useEffect, useState } from 'react';
import CarServices from '../../services/CarService';

const CarList = () => {
  const [cars, setCars] = useState([]); // Utilisation de useState pour stocker les voitures
  const [loading, setLoading] = useState(true); // Pour afficher un état de chargement
  const [error, setError] = useState(null); // Pour gérer les erreurs éventuelles

  // Utilisation de useEffect pour charger les données à la première montée du composant
  useEffect(() => {
    const loadCars = async () => {
      try {
        const response = await CarServices.findALL(); // Appel à l'API pour récupérer les voitures
        setCars(response.data); // Mise à jour de l'état avec les données des voitures
        setLoading(false); // Fin du chargement
      } catch (error) {
        setError("Erreur lors du chargement des voitures");
        setLoading(false); // Fin du chargement même en cas d'erreur
      }
    };

    loadCars(); // Appel de la fonction pour charger les voitures
  }, []); // [] assure que l'effet ne se déclenche qu'une fois à la première montée du composant

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Liste des Voitures</h1>
      {cars.length > 0 ? (
        <table border="1" cellPadding="10">
          <thead>
            <tr>
              <th>Marque</th>
              <th>Modèle</th>
              <th>Matricule</th>
              <th>Client</th>
              <th>Âge du Client</th>
            </tr>
          </thead>
          <tbody>
            {cars.map((car) => (
              <tr key={car.id}>
                <td>{car.marque}</td>
                <td>{car.model}</td>
                <td>{car.matricule}</td>
                <td>{car.client.nom}</td>
                <td>{car.client.age}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Aucune voiture disponible.</p>
      )}
    </div>
  );
};

export default CarList;
