import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import CarServices from "../../services/CarService";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
export default function AddCar() {
    const [clientId, setClientId] = useState('');
    const [marque, setMarque] = useState('');
    const [model, setModel] = useState('');
    const [matricule, setMatricule] = useState('');
    const [car, setCar] = useState({
        marque: "",
        model: "",
        matricule: ""
    });
    const [addCar] = CarServices.saveClient(car, clientId);
    const navigate = useNavigate();
    const handleSave = (e) => {
        e.preventDefault();
        addCar.then(() => {
            navigate('/');
            toast.success('Voiture ajoutée avec succès!', {
                position: "top-right",
                autoClose: 3000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
            });
            
    }).catch(error => {
        console.error("Erreur d'enregistrement :", error);
        toast.error("Erreur lors de l'enregistrement ...!!", {
            position: "top-right",
            autoClose: 3000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
        });
    }); 
    };
    return(
        <div>
            <h3>Ajout d'une nouvelle voiture</h3>
            <form onSubmit={handleSave}>
            
            <h3>Ajouter une nouvelle voiture</h3>
          <div className="form-group">
            <label>Marque </label>
            <input
              type="text"
              value={car.marque}
              className="form-control"
              onChange={(e) => setMarque(e.target.value)}
              required
            />
          </div>
          <br/>
          <div className="form-group">
            <label>Modele </label>
            <input
              type="text"
              value={car.model}
              className="form-control"
              onChange={(e) => setModel(e.target.value)}
              required
            />
            <br/>
            <div className="form-group">
            <label>Marque </label>
            <input
              type="text"
              value={car.matricule}
              className="form-control"
              onChange={(e) => setMarque(e.target.value)}
              required
            />
          </div>
          <br/>
          </div>
          <br/>
          <div className="form-group">
            <label>Utilisateur</label>
            <select value={type} onChange={(e) => setType(e.target.value)} className="form-control">
              <option value="COURANT">COURANT</option>
              <option value="EPARGNE">EPARGNE</option>
            </select>
          </div>
          <br/>
          <button type="submit" className="btn btn-primary">Ajouter un nouveau compte</button>
          
        </form>
        </div>
    );
}
